/**/
function getLocation(){
if(navigator.geolocation){
navigator.geolocation.getCurrentPosition(savePosition,
positionError, {timeout:10000});
}else{

}
return false;
}

/**/
function positionError(error){
var errorCode = error.code;
var message = error.message;
alert(message);
}

/**/
function savePosition(position){
$.post("ajax", {lat: position.coords.latitude, lng:
position.coords.longitude,action:'getUserGoogleLocation'},function(result){
if(result != ""){
$("#postal_address").val(result.address);
$("#city").val(result.city_name);
$("#state").val(result.state_name);
$('#country').text(result.country_name);
}
},"json");
}

/**/
function initAutocomplete() {
if($("#postal_address").length){
autocomplete = new google.maps.places.Autocomplete(
(document.getElementById('postal_address')),{fields:
["address_components", "geometry"], types:
['establishment']});
autocomplete.addListener('place_changed',
fillInAddress);
}
}

/**/
function fillInAddress() {
var place = autocomplete.getPlace();
for (var component of place.address_components) {
var addressType = component.types[0];
if (addressType == "locality" || addressType ==
"postal_town") {
const val = component[componentForm[addressType]];
document.getElementById("city").value = val;
}
if (addressType == "administrative_area_level_1") {
const val = component[componentForm[addressType]];
document.getElementById("state").value = val;
}
if (addressType == "country") {
const val = component[componentForm[addressType]];
alert(val);
document.getElementByName("country").value = val;
}
}
}

$("select[id^='program_priority_no_']").change(function()
{
var choice_object = $(this), selections = [];
$("select[id^='program_priority_no_']
option:selected").each(function(){
if($(this).val())
selections.push($(this).val());
});
//
$("select[id^='program_priority_no_']
option").each(function() {
$(this).attr('disabled',
$.inArray($(this).val(),selections) > -1 &&
!$(this).is(":selected"));
});
});
